import React, { useContext, useState } from "react";
import { Link } from "react-router-dom";
import { motion } from "framer-motion";
import { transition1 } from "../transitions";
import { CursorContext } from "../context/CursorContext";
import PortfolioCarousel from './PortfolioCarousel';

const Portfolio = () => {
  const { mouseEnterHandler, mouseLeaveHandler } = useContext(CursorContext);
  const [showCarousel, setShowCarousel] = useState(false); // State to control the visibility of the carousel

  const handleOpenCarousel = () => {
    setShowCarousel(true);
  };

  const handleCloseCarousel = () => {
    setShowCarousel(false);
  };

  return (
    <motion.section
      initial={{ opacity: 0, y: "100%" }}
      animate={{ opacity: 1, y: 0 }}
      exit={{ opacity: 0, y: "100%" }}
      transition={transition1}
      className="section"
    >
      <div className="container mx-auto h-full relative">
        <div className="flex flex-col lg:flex-row h-full items-center justify-start gap-x-24 text-center lg:text-left pt-24 lg:pt-36 pb-8">
          {/* Text Section */}
          <motion.div
            onMouseEnter={mouseEnterHandler}
            onMouseLeave={mouseLeaveHandler}
            initial={{ opacity: 0, y: "-80%" }}
            animate={{ opacity: 1, y: 0 }}
            exit={{ opacity: 0, y: "-80%" }}
            transition={transition1}
            className="flex flex-col lg:items-start"
          >
            <h1 className="h1">Portfolio</h1>
            <p className="mb-12 max-w-sm">
              Welcome to my portfolio, where I share my journey as a
              videographer. Here, you'll find a collection of videos that
              capture moments, stories, and emotions. Each piece reflects my
              passion for visual storytelling and the art of filmmaking. I
              invite you to explore these videos and 
              <b 
                onClick={handleOpenCarousel} 
                className="text-primary cursor-pointer underline">
                experience the narratives
              </b> 
              they bring to life. 
              <br />
              <br />
            </p>
            <Link to={"/contact"} className="btn mb-[30px] mx-auto lg:mx-0">
              Hire me
            </Link>
          </motion.div>

          {/* Video Grid Section */}
          <div
            onMouseEnter={mouseEnterHandler}
            onMouseLeave={mouseLeaveHandler}
            className="grid grid-cols-2 lg:gap-2"
          >
            {/* Video Thumbnails */}
            {/* ...existing video grid code... */}
          </div>
        </div>

        {/* Modal for Carousel */}
        {showCarousel && (
          <div 
            className="fixed inset-0 bg-black bg-opacity-75 flex justify-center items-center z-50"
            onClick={handleCloseCarousel}
          >
            <div 
              className="relative bg-white p-4 rounded-lg max-w-3xl mx-auto"
              onClick={(e) => e.stopPropagation()}
            >
              <button 
                className="absolute top-2 right-2 text-gray-500 hover:text-gray-800"
                onClick={handleCloseCarousel}
              >
                &times; Close
              </button>
              <PortfolioCarousel />
            </div>
          </div>
        )}
      </div>
    </motion.section>
  );
};

export default Portfolio;
