import React, { useContext } from "react";
// import images
import Image1 from '../img/portfolio/1.png';
import Image2 from '../img/portfolio/2.png';
import Image3 from '../img/portfolio/3.png';
import Image4 from '../img/portfolio/4.png';
import WomanImg from "../img/about/saied.png";
// import link
import { Link } from "react-router-dom";
// import motion
import { motion } from "framer-motion";
// import transition
import { transition1 } from "../transitions";
// import cursor context
import { CursorContext } from "../context/CursorContext";

const About = () => {
  const { mouseEnterHandler, mouseLeaveHandler } = useContext(CursorContext);
  return (
    <motion.section
      initial={{ opacity: 0, y: "100%" }}
      animate={{ opacity: 1, y: 0 }}
      exit={{ opacity: 0, y: "100%" }}
      transition={transition1}
      className="section"
    >
      <div
        onMouseEnter={mouseEnterHandler}
        onMouseLeave={mouseLeaveHandler}
        className="container mx-auto h-full relative"
      >
        {/* text & img wrapper */}
        <div className="flex flex-col lg:flex-row h-full items-center justify-center gap-x-24 text-center lg:text-left lg:pt-16">
          {/* image */}
          <div className="flex-1 max-h-96 lg:max-h-max order-2 lg:order-none overflow-hidden">
            <img src={WomanImg} alt="" />
          </div>
          {/* text */}
          <motion.div
            initial={{ opacity: 0, y: "-80%" }}
            animate={{ opacity: 1, y: 0 }}
            exit={{ opacity: 0, y: "-80%" }}
            transition={transition1}
            className="flex-1 pt-36 pb-14 lg:pt-0 lg:w-auto z-10 flex flex-col justify-center items-center lg:items-start"
          >
            <h1 className="h1">About me</h1>
            <p className="mb-12 max-w-sm">
              I am a fanatic of film and storytelling mostly in the form of
              drama and rich character developments and visuals. I believe film
              is distinct from movies because it as a whole is a totally
              different feeling that ultimately determines the 
              
              <b> final experience </b>
              as a viewer. 
              <br />
              <br />
              As a cinematographer, I am in charge of leading the
              dance for the audience. I control how you feel based on the
              motions of the camera.
            </p>
            
            <Link to={"/portfolio"} className="btn">
              View my work
            </Link>
            
          </motion.div>
        </div>

        {/* image grid */}
        <div
          onMouseEnter={mouseEnterHandler}
          onMouseLeave={mouseLeaveHandler}
          className='grid grid-cols-2 lg:gap-2 mt-12'
        >
          {/* image */}
          <div className='max-w-[250px] lg:max-w-[320px] h-[187px] lg:h-[220px] bg-accent overflow-hidden'>
            <img
              className='object-cover h-full lg:h-[220px] hover:scale-110 transition-all duration-500'
              src={Image1}
              alt=''
            />
          </div>
          <div className='max-w-[250px] lg:max-w-[320px] h-[187px] lg:h-[220px] bg-accent overflow-hidden'>
            <img
              className='object-cover h-full lg:h-[220px] hover:scale-110 transition-all duration-500'
              src={Image2}
              alt=''
            />
          </div>
          <div className='max-w-[250px] lg:max-w-[320px] h-[187px] lg:h-[220px] bg-accent overflow-hidden'>
            <img
              className='object-cover h-full lg:h-[220px] hover:scale-110 transition-all duration-500'
              src={Image3}
              alt=''
            />
          </div>
          <a href="https://the2ampodcast.com" target="_blank" rel="noopener noreferrer">
            <div className='max-w-[250px] lg:max-w-[320px] h-[187px] lg:h-[220px] bg-accent overflow-hidden'>
              <img
                className='object-cover h-full lg:h-[220px] hover:scale-110 transition-all duration-500'
                src={Image4}
                alt=''
              />
            </div>
          </a>
        </div>
        
      </div>
      
    </motion.section>
  );
};

export default About;
