import React, { useContext } from "react";
// import images
import WomanImg from "../img/about/saied.png";
import Image1 from '../img/about/saied1.png'; // Example images
import Image2 from '../img/gallery/photo2.png'; // Add your image paths here
import Image3 from '../img/gallery/photo3.png'; // Add more images as needed
// import link
import { Link } from "react-router-dom";
// import motion
import { motion } from "framer-motion";
// import transition
import { transition1 } from "../transitions";
// import cursor context
import { CursorContext } from "../context/CursorContext";

const About = () => {
  const { mouseEnterHandler, mouseLeaveHandler } = useContext(CursorContext);

  return (
    <>
      <motion.section
        initial={{ opacity: 0, y: "100%" }}
        animate={{ opacity: 1, y: 0 }}
        exit={{ opacity: 0, y: "100%" }}
        transition={transition1}
        className="section"
      >
        <div
          onMouseEnter={mouseEnterHandler}
          onMouseLeave={mouseLeaveHandler}
          className="container mx-auto h-full relative"
        >
          {/* text & img wrapper */}
          <div className="flex flex-col lg:flex-row h-full items-center justify-center gap-x-24 text-center lg:text-left lg:pt-16">
            {/* image */}
            <div className="flex-1 max-h-96 lg:max-h-max order-2 lg:order-none overflow-hidden">
              <img src={WomanImg} alt="" />
            </div>
            {/* text */}
            <motion.div
              initial={{ opacity: 0, y: "-80%" }}
              animate={{ opacity: 1, y: 0 }}
              exit={{ opacity: 0, y: "-80%" }}
              transition={transition1}
              className="flex-1 pt-36 pb-14 lg:pt-0 lg:w-auto z-10 flex flex-col justify-center items-center lg:items-start"
            >
              <h1 className="h1">About me</h1>
              <p className="mb-12 max-w-sm">
                I am a fanatic of film and storytelling mostly in the form of
                drama and rich character developments and visuals. I believe film 
                stands apart from movies, offering a completely unique and immersive 
                feeling that ultimately determines the final experience as a viewer. 
                As a cinematographer, I am in charge of leading the
                dance for the audience. I control how you feel based on the
                motions of the camera. In my personal life, capturing moments has 
                become more than just a hobby—
                <b> it's a natural extension of who I am.</b> 
                <br />
                <br />
                I love love love capturing people in their
                most dream-like state to create a feeling of “missing” or
                nostalgia. After all, a photo IS a memory.
              </p>
              <Link to={"/portfolio"} className="btn">
                View my work
              </Link>
            </motion.div>
          </div>
        </div>
      </motion.section>

      {/* Spacer */}
      <div className="h-16"></div>

      {/* New Gallery Section */}
      <section className="gallery-section">
        <div className="container mx-auto px-4">
          <h2 className="text-center text-3xl mb-8">Gallery</h2>
          <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-4">
            <div className="overflow-hidden">
              <img
                className="object-cover w-full h-full hover:scale-110 transition-all duration-500"
                src={Image1}
                alt="Gallery Image 1"
              />
            </div>
            <div className="overflow-hidden">
              <img
                className="object-cover w-full h-full hover:scale-110 transition-all duration-500"
                src={Image2}
                alt="Gallery Image 2"
              />
            </div>
            <div className="overflow-hidden">
              <img
                className="object-cover w-full h-full hover:scale-110 transition-all duration-500"
                src={Image3}
                alt="Gallery Image 3"
              />
            </div>
            {/* Add more images as needed */}
          </div>
        </div>
      </section>
    </>
  );
};

export default About;
